// Categorize files by extension
const items = $input.all();
const categorized = [];

for (const item of items) {
  const fileName = item.json.fileName || item.json.name || '';
  const extension = fileName.split('.').pop().toLowerCase();

  let category = 'other';

  if (['jpg', 'jpeg', 'png', 'gif', 'bmp', 'svg'].includes(extension)) {
    category = 'images';
  } else if (['pdf', 'doc', 'docx', 'txt', 'odt'].includes(extension)) {
    category = 'documents';
  } else if (['mp4', 'avi', 'mov', 'mkv', 'flv'].includes(extension)) {
    category = 'videos';
  } else if (['mp3', 'wav', 'flac', 'aac', 'ogg'].includes(extension)) {
    category = 'audio';
  } else if (['zip', 'rar', '7z', 'tar', 'gz'].includes(extension)) {
    category = 'archives';
  } else if (['exe', 'dmg', 'app', 'deb', 'rpm'].includes(extension)) {
    category = 'executables';
  }

  categorized.push({
    json: {
      ...item.json,
      category,
      extension,
      originalPath: item.json.path || fileName
    }
  });
}

return categorized;

// Delete files older than 7 days
const items = $input.all();
const now = new Date();
const sevenDaysAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
const filesToDelete = [];

for (const item of items) {
  const modifiedTime = new Date(item.json.modifiedTime || item.json.mtime);

  if (modifiedTime < sevenDaysAgo) {
    filesToDelete.push({
      json: {
        path: item.json.path,
        fileName: item.json.name,
        age: Math.floor((now - modifiedTime) / (1000 * 60 * 60 * 24)) + ' days'
      }
    });
  }
}

return filesToDelete;
